<?php/** * The template for displaying all single posts * * @link https://developer.wordpress.org/themes/basics/template-hierarchy/#single-post * * @package rozer */get_header();$single_post_layout = rdt_get_option('blog_single_layout', 'right-sidebar');//Start demoif(isset($_GET['ps_layout']) && $_GET['ps_layout'] != '') {	$single_post_layout = $_GET['ps_layout'];}//End demoif($single_post_layout == 'right-sidebar' && is_active_sidebar('sidebar-blog')){	$class = 'col-lg-9 col-12 col-md-9';}elseif($single_post_layout == 'left-sidebar' && is_active_sidebar('sidebar-blog')){	$class = 'col-lg-9 col-12  order-lg-last';}else{	$class = 'col-lg-12 col-12 col-md-12';}
$single_post_design = rdt_get_option('blog_single_design', '1');$title_background = rdt_get_option('blog_single_bgtitle', '');$title_class = '';$title_align = rdt_get_option('blog_single_title_align', 'left');//Start demoif(isset($_GET['ps_title']) && $_GET['ps_title'] != '') {	$title_align = $_GET['ps_title'];}//End demo$title_color = rdt_get_option('blog_single_title', 'dark');$title_class .= 'text-'.$title_align;$title_class .= ' text-'.$title_color;$related_active = rdt_get_option( 'blog_single_related' , '1');$related_limit = rdt_get_option('blog_single_related_limit', '4');$related_column = rdt_get_option('blog_single_related_column', '3');?><?php echo rozer_breadcrumb(); ?><?php if($single_post_design == '2') : ?>	<header class="entry-header title-background <?php echo esc_attr($title_class); ?>" 		<?php if($title_background): ?> style= "background-image: url('<?php echo esc_url($title_background); ?>');background-size: cover; " <?php endif; ?>>		<div class="container">			<?php			if ( 'post' === get_post_type() ) :				?>				<div class="post-categories-parent">					<?php					echo get_the_category_list( esc_html__( ', ', 'rozer' ) );					?>				</div>			<?php endif; ?>			<?php			the_title( '<h1 class="entry-title">', '</h1>' );			if ( 'post' === get_post_type() ) :				?>				<div class="entry-meta">					<?php					the_post();					rozer_posted_on();					rozer_posted_by();					/*	                 * Since we called the_post() above, we need to	                 * rewind the loop back to the beginning that way	                 * we can run the loop properly, in full.	                 */	                rewind_posts();					?>				</div><!-- .entry-meta -->			<?php endif; ?>		</div>	</header><!-- .entry-header --><?php endif; ?><div class="container">	<div class="row">		<div id="content" class="<?php echo esc_attr($class); ?>">			<?php			while ( have_posts() ) :				the_post();				get_template_part( 'template-parts/posts/content', 'single' );				the_post_navigation(					array(						'prev_text' => '<span class="nav-subtitle">' . esc_html__( 'Previous', 'rozer' ) . '</span> <span class="nav-title">%title</span>',						'next_text' => '<span class="nav-subtitle">' . esc_html__( 'Next', 'rozer' ) . '</span> <span class="nav-title">%title</span>',					)				);				if ( $related_active == '1' &&  class_exists('Rozer_Core')) : ?>				<div class="related_posts">					<h2 class="title-block"><?php echo esc_html__( 'Related posts', 'rozer' ) ?></h2>				    <?php echo rozer_get_related_posts($post->ID, $related_limit , $related_column); ?>				</div>				<?php endif;				// If comments are open or we have at least one comment, load up the comment template.				if ( comments_open() || get_comments_number() ) :					comments_template();				endif;			endwhile; // End of the loop.			?>		</div>		<?php		if($single_post_layout != 'no-sidebar' && is_active_sidebar('sidebar-blog')){			get_sidebar();		}		?>	</div></div><?php get_footer();